from selenium.webdriver.support.wait import WebDriverWaitfrom selenium.webdriver.support import expected_conditions as ECfrom selenium.webdriver.common.by import Byfrom selenium.webdriver import ActionChainsfrom utilities.Common import getUrlclass WebDriverBase(object):    __TIMEOUT = 100    def __init__(self, web_driver):        super(WebDriverBase, self).__init__()        self._web_driver_wait = WebDriverWait(web_driver, WebDriverBase.__TIMEOUT)        self._web_driver = web_driver        self.ActionChains = ActionChains(self._web_driver)    def open(self):        return self._web_driver.get(getUrl())    def findByXpath(self, xpath):        return self._web_driver_wait.until(EC.visibility_of_element_located((By.XPATH, xpath)))    def findByName(self, name):        return self._web_driver_wait.until(EC.visibility_of_element_located((By.NAME, name)))    def findById(self, id):        return self._web_driver_wait.until(EC.visibility_of_element_located((By.ID, id)))